    constructor() {
        this.options = {};
        this.dashboard = [];
        this.gridCols = 20;
        this.gridRows = 20;
        this.innerWidth = window.innerWidth;
        // 初期化時のinnerWidthによってグリッド列数を変える
        if (1500 <= window.innerWidth) {
            this.gridCols = 30;
        }
        else if (1000 <= window.innerWidth) {
            this.gridCols = 13;
        }
        else {
            this.gridCols = 6;
        }
        ;
    }
    ngOnInit() {
        this.options = {
            gridType: 'scrollVertical',
            // gridType: 'scrollHorizontal', // 固定縦横比を水平スクロールで対応
            draggable: {
                enabled: true,
                ignoreContent: true,
                dragHandleClass: 'draggable-handler', // ここで指定したクラスのみドラッグイベントを可能にする
                // delayStart: 500,
            },
            resizable: {
                enabled: true, // リサイズを許可する
                // delayStart: 500,
            },
            swap: true,
            displayGrid: 'always',
            minCols: this.gridCols,
            maxCols: this.gridCols,
            minRows: this.gridRows,
            maxRows: this.gridRows,
            maxItemCols: 5,
            maxItemRows: 5,
            compactType: 'none',
            pushItems: true,
            mobileBreakpoint: 640, // 画面の幅が640px以下でグリッドを解除しアイテムを1列にする
        };
        this.dashboard = [
            { cols: 3, rows: 4, y: 1, x: 2 }, // 初期値 横3マス, 縦4マス,をy1, x2の位置に配置
        ];
    }
    removeItem(index) {
        this.dashboard.splice(index, 1); // index番目を1つ取り除く
    }
    addItem() {
        this.dashboard.push({ cols: 3, rows: 3, y: 0, x: 0 });
    }
    async onClick() {
        await this.drawer.toggle();
        // this.options.api?.resize?.();
    }
}
